pub struct NotificationResult {
    pub handled: bool,
}

pub fn notify_dismissed(app: &AppHandle, handled: bool) {
    if let Err(err) = app.emit(
        "gmail://notification-complete",
        &NotificationResult { handled },
    ) {
        warn!(%err, "failed to notify dismissal");
    }
}

